// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract DuelArena is ReentrancyGuard, Ownable {
    struct Room {
        address creator;
        address challenger;
        uint256 betAmount;
        bool gameFinished;
        address winner;
        uint256 createdAt;
    }

    mapping(uint256 => Room) public rooms;
    mapping(address => uint256[]) public userRooms;
    uint256 public nextRoomId;
    
    uint256 public constant TREASURY_FEE_PERCENT = 5; // 5% fee
    uint256 public constant MIN_BET = 0.001 ether;
    uint256 public constant MAX_BET = 0.005 ether;

    event RoomCreated(uint256 indexed roomId, address indexed creator, uint256 betAmount);
    event RoomJoined(uint256 indexed roomId, address indexed challenger, uint256 betAmount);
    event GameFinished(uint256 indexed roomId, address indexed winner);
    event RewardClaimed(uint256 indexed roomId, address indexed winner, uint256 amount);
    event TreasuryFeeCollected(uint256 indexed roomId, uint256 amount);

    constructor() Ownable(msg.sender) {}

    /**
     * @dev Create a new room with a bet amount
     * @param betAmount The bet amount in wei (must be 0.001 or 0.005 ETH)
     */
    function createRoom(uint256 betAmount) external payable nonReentrant {
        require(betAmount == MIN_BET || betAmount == MAX_BET, "Invalid bet amount");
        require(msg.value == betAmount, "Sent amount must match bet amount");
        require(msg.sender != address(0), "Invalid creator address");

        uint256 roomId = nextRoomId++;
        rooms[roomId] = Room({
            creator: msg.sender,
            challenger: address(0),
            betAmount: betAmount,
            gameFinished: false,
            winner: address(0),
            createdAt: block.timestamp
        });

        userRooms[msg.sender].push(roomId);

        emit RoomCreated(roomId, msg.sender, betAmount);
    }

    /**
     * @dev Join an existing room with matching bet amount
     * @param roomId The ID of the room to join
     */
    function joinRoom(uint256 roomId) external payable nonReentrant {
        Room storage room = rooms[roomId];
        require(room.creator != address(0), "Room does not exist");
        require(room.challenger == address(0), "Room already has a challenger");
        require(msg.value == room.betAmount, "Sent amount must match room bet amount");
        require(msg.sender != room.creator, "Cannot join your own room");
        require(msg.sender != address(0), "Invalid challenger address");

        room.challenger = msg.sender;
        userRooms[msg.sender].push(roomId);

        emit RoomJoined(roomId, msg.sender, room.betAmount);
    }

    /**
     * @dev Mark game as finished and set winner (can be called by anyone, but should be called by backend/game logic)
     * @param roomId The ID of the room
     * @param winner The address of the winner (must be creator or challenger)
     */
    function finishGame(uint256 roomId, address winner) external {
        Room storage room = rooms[roomId];
        require(room.creator != address(0), "Room does not exist");
        require(room.challenger != address(0), "Room has no challenger");
        require(!room.gameFinished, "Game already finished");
        require(winner == room.creator || winner == room.challenger, "Winner must be creator or challenger");

        room.gameFinished = true;
        room.winner = winner;

        emit GameFinished(roomId, winner);
    }

    /**
     * @dev Claim reward after winning the game
     * @param roomId The ID of the room
     */
    function claimReward(uint256 roomId) external nonReentrant {
        Room storage room = rooms[roomId];
        require(room.gameFinished, "Game not finished");
        require(room.winner == msg.sender, "Only winner can claim reward");
        require(room.winner != address(0), "No winner set");

        uint256 totalBet = room.betAmount * 2; // Creator + Challenger bets
        uint256 treasuryFee = (totalBet * TREASURY_FEE_PERCENT) / 100;
        uint256 reward = totalBet - treasuryFee;

        // Mark room as claimed by resetting winner (prevent double claim)
        room.winner = address(0);

        // Transfer reward to winner
        (bool success, ) = payable(msg.sender).call{value: reward}("");
        require(success, "Transfer failed");

        emit RewardClaimed(roomId, msg.sender, reward);
        emit TreasuryFeeCollected(roomId, treasuryFee);
    }

    /**
     * @dev Get room information
     * @param roomId The ID of the room
     * @return Room struct with all room information
     */
    function getRoom(uint256 roomId) external view returns (Room memory) {
        return rooms[roomId];
    }

    /**
     * @dev Get all room IDs for a user
     * @param user The user address
     * @return Array of room IDs
     */
    function getUserRooms(address user) external view returns (uint256[] memory) {
        return userRooms[user];
    }

    /**
     * @dev Withdraw treasury fees (only owner)
     */
    function withdrawTreasury() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");
        
        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "Withdrawal failed");
    }

    /**
     * @dev Get contract balance
     * @return The contract balance in wei
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

